import React, { useState } from 'react';

function App() {
  const [modal1, setModal1] = useState(false);

  const handleClick1 = () => {
    event.preventDefault()
    setModal1(!modal1)
  }
  return (
    <>
      <div style={{ textAlign: 'center', paddingTop: '100px' }}>
        <button onClick={handleClick1}>Modal1</button>
        <button>Modal2</button>
      </div>
      <div>
        {modal1 ?
          <h1>Teste</h1>
          : null
        }
      </div>
    </>
  );
};

export default App;
